import Slidertools from '../components/Slidertools';

const { TabPane } = Tabs;

export default function Instrumentation({params,functions,toolType,names}){
  const [visible, setVisible] = React.useState(false);
  const showDrawer = () => {
    if(window.innerWidth<=700)
    setVisible(true);
  };

  const onClose = () => {
    setVisible(false);
  };
  React.useEffect(()=>{
    if(window.innerWidth>700)
      setVisible(false)
  })
    const [question,setQuestion] = React.useState();

    const [result,setResult] = React.useState(0);
    const [resultn,setResultn] = React.useState();

    let vari;
    const [queryid,setQueryId] = React.useState()
    const router = useRouter()
    const [array,setarray] = React.useState(names)
   const [tooltype,setTooltype] = React.useState(toolType)
   // let vari=  functions.find(item => item.stringId === queryid.toString())

    const setFn = (value) => {
        // setQuestion(newValue)
        setResultn(1);
         setResult((value-a)*100/(b-a))
        setQuestion(value)
        console.log(value)
        document.getElementById('InputID').value = ''

      }
      const [a,seta] = React.useState()
      const [b,setb] = React.useState()

      const setIn = (e) => {
        setResultn(1);

        console.log(e.target.value)
          if((e.target.value)!== NaN)
         { setQuestion(Number(e.target.value))
          setResult((Number(e.target.value)-a)*100/(b-a))}

      }
      const setRn = (e) => {
        setResultn(1);

        if((e.target.value)!== NaN)
        {
        setResult(e.target.value)
        setQuestion(Number(e.target.value)*(b-a)/100 + a)}

      }
      const resultfn = (value) => {
        setResultn(1);

        setResult(value)
        setQuestion(value*(b-a)/100 + a)

      }

      React.useEffect(()=> {
        if(router && router.query) {
          setQueryId(router.query.name)
          console.log("router",router.query.id)

        }
      }, [router,router.query])
      const [display,setDisplay] = React.useState()
      const [marks,setMarks]= React.useState({0:"0",100:"100"});
      const [name,setName]= React.useState();
      const [title,setTitle]= React.useState();
React.useEffect(()=>{
        if(queryid!==undefined){

            vari = functions.find(item=> stringSpaceToHyphen(item.display) === router.query.name)
            seta(vari.a)
            setName(vari.name)
            setb(vari.b)
            setTitle(router.query.name)
            setDisplay(vari.display)
            setQuestion(functions.find(item =>stringSpaceToHyphen(item.display) === router.query.name).a)

          }
      },[queryid])


      React.useEffect(()=>{
        if(a!==NaN && a!==undefined && b!==NaN && b!==undefined)
        {  

            let anumber = parseInt(a)
            let astring = a.toString()
           console.log("a is",a,"b is",b)
           console.log(typeof(anumber),typeof(astring))
           if(!isNaN(a))
           setMarks(
            {a:"hello",b:"h"}
           )
        }
      },[a,b])

    return(
      <div>
        <Head>
          <title>{router.query.name}</title>
          <link href="https://fonts.googleapis.com/icon?family=Material+Icons" rel="stylesheet"/>

          {functions!==undefined && functions.map(item => stringSpaceToHyphen(item.display) === router.query.name
          && <meta key="description" name="description"content={item.desc}/>) }
        </Head>
        <Navbar showDrawer={showDrawer}/>


        <div className="tools-container">
            {/* sliding drawer */}
            <Drawer width={350} title="gudofConvert" placement="left" closable={true} onClose={onClose} visible={visible}>
              <DrawerNav names={names} toolType={toolType} onClose={()=>setVisible(false)}/>
            </Drawer>

            {/* fixed side menu */}
            <div className="tools-left">
              <SideNav names={names} toolType={toolType}/>
            </div>

            {/* toolpart */}
            <Layout className="toolbox-layout">
                {question!==undefined &&
                    <Slidertools display={display} a={a} b={b} res={result} qn={question} name={name}/>
                }
               </Layout>
           </div>
        </div>
    )
}

