 import '../styles/globals.less';
import '../styles/Homepage.less';
import '../styles/menu.less';
// import '../styles/Home.module.less';
import '../styles/tools.less';
import {Layout } from 'antd';
function MyApp({ Component, pageProps }) {
  return <Layout>
     <style jsx global>
            {
              `

.card {
  padding:20px;
  margin:10px; 
}
.ant-tabs-ink-bar{
  background: #F73406 !important;
}
.ant-tabs-tab.ant-tabs-tab-active .ant-tabs-tab-btn {
  color:#F73406 !important;
}
.ant-tabs-tab:hover .ant-tabs-tab:hover .ant-tabs-tab-btn:hover {
  color:#F73406 !important;
}
.heading {
  font-size: larger;
  text-transform: uppercase;
  font-weight: bold;
  color:#F73406;
}
.center {
  display:flex;
  align-items: center;
  align-content: center;

}
html, .tools-container,.body {
  --antd-wave-shadow-color: #F73406 !important;
  --scroll-bar: 0;
  font-family: 'Nexalight' !important;
}
.h2 {
  margin-left: 20px;
  color:white;
  margin-top:10px !important;
}
.h2-nav {
  margin-left: 20px;
  color:white;
  margin-top:20px !important;
  margin-bottom:20px !important;
}
.input {
  border:1px solid gray;
  padding:5px;
  margin-right: 20px;
}
.result-heading {
  font-weight:bold;
  color:#F73406;
  text-transform: uppercase;
}
.result {
  margin:20px 0px;
  padding:20px;
  border:solid 1px #F73406;
  width:100%;
}
a {
  color: inherit;
  text-decoration: none;
}

* {
  box-sizing: border-box;
}

.appbar {
  background:#F73406;
}
.descp {
  font-style: italic;
}

.contain {
  display: flex;
}
.ant-tabs-content-holder {
  display: flex;
}
.ant-tabs {

  width:100% !important;
}
.ant-input-affix-wrapper:hover,.ant-input-affix-wrapper:hover:focus {
  border:1px solid lightgray !important;
  box-shadow: 0px 0px 0px 0px;
}
input[type="text"]:focus {
  border:0 !important;
  box-shadow: 0px 0px 0px 0px
   !important;
}
.ant-tabs-tab {
  flex-grow: 1;
  margin-left: 20px !important;
}
.input {
  margin-left:25px;
  margin-right:30px;
}
@font-face {
  font-family: 'Nexabold';
    src: url('../public/fonts/Nexa\ Bold.otf') format('opentype');
}
@font-face {
  font-family: 'Nexalight';
    src: url('../public/fonts/Nexa\ Light.otf') format('opentype');
}

.input-col , .input-col input {
  min-width:60px;
  width:100px;
  margin-right:30px;
}
.slider {
  float:right;
  right:0;
}
.center {
  display: inline;
}
.icon-wrapper {
  position: relative;
  padding: 0px 30px;
}

.ant-menu a {
  color:white !important;
}
.ant-layout {
  background: white !important;
}
a {color:black !important;}
.ant-card:hover a {
  color:black !important;
}
.ant-card , .ant-card a{
  transition: cubic-bezier(0.77, 0, 0.175, 1) 0.5s;
}
.ant-card {
  background: #fcfcfc !important;
  border-radius: 10px !important;
}
.ant-input-affix-wrapper,.ant-input-affix-wrapper:hover,.ant-input-affix-wrapper:focus {
  box-shadow: 0 !important;
  border:0 !important;}
 .ant-tabs-tab-btn:hover , .ant-tabs-tab:hover {
  color:#F73406 !important;
}
.ant-input:focus {
  border:1px solid gray !important;
}
.ant-card {
  margin-top: 20px;
}
.card , .carditems,.ant-card-body,.ant-card {
  color:black !important;
}
.ant-slider-track {
  background:#F73406 !important;
  }
  .ant-slider-handle {
    border:2px solid #F73406 !important;
  }


  .appBar  {
    background:red;
    color:red;
  }

  .taabs {
        color:red;
        display: inline;
  }

  .left {
      float:left;
  }
  .tab {
      float:left;width:50%;
  }

  .menu {
      background:red;
      color:white;
      width:100%;

  }
  .input {
      min-width:100px;
  }
  .contain {
      display: inline;
  }
  .carditems {
      max-width:90%;
      min-width: 30%;
      width:350px;
      padding:10px;
      margin:10px;
  }

  @media only screen and (max-width: 600px) {
    .carditems {
     min-width:250px;
    }
  }
  .carditems:hover {
    box-shadow: 0px 4px 8px 0px rgba(0,0,0,0.5);

  }
  .tool {
      width:100%;
      padding:50px 100px;
      display:flex;
      align-self: center;
  }

  .row {
      margin-bottom:20px;
  }

  .navmenu {
    min-width:300px;
    max-width:400px;
    background:white;

}

.navtitle {
   margin-left: 10px;
    float:left;
    width:175px;


}
.navtitle-icon {

    margin-right: 10px;
}
.h {
    min-height:40px;
}
.nav-item {

    padding-left:20px;
    margin:3px;
    height:30px;
    width:100%;
    transition:0.3s;
}
.nav-item:hover {
    background: #f1f1f1;

}
.nav-item:hover a {
    color:black !important;
}
.menu {
    height:60px;
}

.toolbox {
  width:100%;
  align-self: center;
  margin:50px 70px;
  margin-top:20px;
  padding:0px !important;
  border-radius: 30px;
  border:0px !important;
}
.ant-card-body {
  padding:0px !important;
  margin-top:20px;
}
.toolbox-heading {
  color:black;
  font-weight: bold;
  font-size: 20px;
  padding:20px;
}

.toolbox-calc {
  padding:20px;
  background: white;
  font-size:15px !important;
}

.toolbox-desc {
  padding:20px;

  border-radius: 0px 0px 20px 20px;
}

.toolbox-layout {
  background: white !important;
  height:100vh;
  margin-top: 60px;
  color:black !important;
  font-size: 17px;

}
.cardwidth {
  min-width:100% !important;
}
.hr {
  size:0.25px;
  width:95%;
  color:lightgray;
}


.tools-container {
  display:flex;
}


.center {
  display:flex;
  align-items: center;
}
.tools-left {
  height:100vh;
  min-width:300px;
  max-width: 50%;
  overflow:auto;
}
.tools-container {
  height:90vh;
}
.menu {
  position:sticky;
}

@media screen and (max-width:700px) {
  .tools-left {
      display:none;
  }
.menu-icon {
    display: block !important;
}
}
.menu-icon {
  display: none;
}
.nav-surround {   
 display: inline;

}
.ant-card-body {
  margin:0 !important;
}
.carditems {
  padding:10px !important;
  font-size:17px !important;
}
@media screen and (max-width:700px) {
  .toolbox {
      margin:0px;
      border:0;
      border-radius:0;
  }
  .input,.input-col input{
      width:60px !important;
      margin-right:10px;
  }
  .input-col {
      min-width:10px;
  }
  .toolbox-heading {
      border-radius:0;}

      .card , .carditems,.ant-card-body ,.ant-card-borde#F73406,.ant-card{
          width:100% !important;
      }
}
.menu {
  display:flex;
  align-items: center;
}
.menu-icon {
  float:left;
  margin-left: 20px;
}
.navbar {
  position:fixed;
  top:0;
  width:100%;
  overflow:hidden;
  z-index:1;
  height:60px;
}
.tools-container {
  margin-top:0px;
  /* overflow:hidden; */

}
.h2-nav {
  font-family: 'Nexabold' !important;
}
.ant-drawer-header ,.ant-drawer-title  {
  background:#F73406 !important;
  color:white !important;
  font-size: 17px  !important;
  font-weight: bold  !important;
}
.calculate-button {
  background:#F73406 !important;
  color:white !important;
  display: flex;
  align-items: center;
}
.toolbox-layout {
  height:100%;
  overflow:auto;
}

.container {
  min-height: 100vh;
  padding: 0 0.5rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.main {
  padding: 5rem 0;
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.toolbar-background {
  background:red;
}
.header {
  display:flex;
  flex-direction:row;
  align-content:center;
  justify-content: center;
  background: red;
}
.red {
  background: red;
  color:white
}

.footer {
  width: 100%;
  height: 100px;
  border-top: 1px solid #eaeaea;
  display: flex;
  justify-content: center;
  align-items: center;
}

.footer img {
  margin-left: 0.5rem;
}

.footer a {
  display: flex;
  justify-content: center;
  align-items: center;
}

.title a {
  color: #0070f3;
  text-decoration: none;
}

.title a:hover,
.title a:focus,
.title a:active {
  text-decoration: underline;
}

.title {
  margin: 0;
  line-height: 1.15;
  font-size: 4rem;
}

.title,
.description {
  text-align: center;
}

.description {
  line-height: 1.5;
  font-size: 1.5rem;
}

.code {
  background: #fafafa;
  border-radius: 5px;
  padding: 0.75rem;
  font-size: 1.1rem;
  font-family: Menlo, Monaco, Lucida Console, Liberation Mono, DejaVu Sans Mono,
    Bitstream Vera Sans Mono, Courier New, monospace;
}

.grid {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;

  max-width: 800px;
  margin-top: 3rem;
}

.card {
  margin: 1rem;
  flex-basis: 45%;

  text-align: left;
  color: inherit;
  text-decoration: none;
  border: 1px solid #eaeaea;
  border-radius: 10px;
  transition: color 0.15s ease, border-color 0.15s ease;
}

.card:hover,
.card:focus,
.card:active {
  color: #0070f3;
  border-color: #0070f3;
}

.card h3 {
  margin: 0 0 1rem 0;
  font-size: 1.5rem;
}

.card p {
  margin: 0;
  font-size: 1.25rem;
  line-height: 1.5;
}

.logo {
  height: 1em;
}

@media (max-width: 600px) {
  .grid {
    width: 100%;
    flex-direction: column;
  }
}
.body {
  font-family: 'Nexabold' !important;
}

 `
            }
          </style>
    <Component {...pageProps} /></Layout>
}

export default MyApp
